mapper.select:
  		select * from(
	    select  H_ID, TELNUM, EXAM_ID, EXAM_NAME, EXAM_TYPE, INDEXNUM, URL, TOTALSCORE, SURPLUSTIME, 
    			USEDTIME, CREATE_TIME, UPDATE_TIME, ANSWER_RECORD ,STARTTIME,STATUS,SUBMIT_TYPE,ISEXAM,rownum as rown
    	from (
    		select H_ID, TELNUM, EXAM_ID, EXAM_NAME, EXAM_TYPE, to_number(INDEXNUM)+1 as INDEXNUM, URL, TOTALSCORE, SURPLUSTIME, 
    				USEDTIME, CREATE_TIME, UPDATE_TIME, ANSWER_RECORD ,to_char(CREATE_TIME,'YYYY-MM-DD') as STARTTIME ,STATUS,SUBMIT_TYPE,ISEXAM
    		from AF_USER_EXAM_HISTORY t 
    		where 
    			t.TELNUM = #{telnum,jdbcType=VARCHAR} 

    			and t.ISEXAM = #{isexam,jdbcType=INTEGER}

    		order by t.create_time desc
    	)) where rown >#{start,jdbcType=INTEGER}  and rown<= #{end,jdbcType=INTEGER}

select count(*) 
from AF_USER_EXAM_HISTORY 
where TELNUM = #{telnum,jdbcType=VARCHAR} 
and TOTALSCORE >= #{score,jdbcType=INTEGER}

select H_ID, TELNUM, EXAM_ID, EXAM_NAME, EXAM_TYPE, to_number(INDEXNUM)+1 as INDEXNUM, URL, TOTALSCORE, SURPLUSTIME, 
		USEDTIME, CREATE_TIME, UPDATE_TIME, ANSWER_RECORD ,to_char(CREATE_TIME,'YYYY-MM-DD') as STARTTIME ,STATUS,SUBMIT_TYPE,ISEXAM
from AF_USER_EXAM_HISTORY t 
where 
		t.TELNUM = #{telnum,jdbcType=VARCHAR} 
	AND t.EXAM_ID = #{examId,jdbcType=VARCHAR}

	AND t.STATUS =#{status,jdbcType=INTEGER} 

order by t.create_time desc
